import React, { useState } from "react";
import { Link, useParams } from "react-router-dom";

const UpdateCategory = ({taskLists,updateCategory}) => {
  const { taskID } = useParams();
  const taskCopy=[...taskLists];
  const[category,setCategory]=useState({
    id:taskID,
    name:'',
    tasks:[{}]

  });
  const editCategory = taskCopy.filter(task=>task.id===taskID).flat()
  const categoryObj= Object.assign({},...editCategory)
  const [categoryToEdit,setCategoryToEdit]=useState(categoryObj.name)
  const getInput = (e)=>{
    let input = e.target.value;
    setCategoryToEdit({...category,name:input})
  }

  const saveEdit = ()=>{
    console.log(category)
  }

  return (
    <div className="overlay">
      <div className="modal">
        <div className="modal-title ">
          <h1>UPDATE CATEGORY:</h1>
          <hr></hr>
        </div>
        <div className="modal-body">
          <div>
            <form autocomplete="off">
              <label>New Category Name:</label>
              <input type="text" name="name" value={categoryToEdit}  onChange={getInput}></input>
            </form>
          </div>

          <div></div>
        </div>
        <div className="modal-footer">
          <Link to={"/all/" + taskID} className="modal-cancel">
            Cancel
          </Link>
          <button className="modal-ok" onClick={saveEdit}>
            Save
          </button>
        </div>
      </div>
    </div>
  );
};

export default UpdateCategory;
