import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCirclePlus } from "@fortawesome/free-solid-svg-icons";
import Tasks from "./Tasks";
const Main = ({ subtasks, task }) => {
  const [taskCount,setTaskCount]= useState(0)
  const getTaskCount = (count) =>{
    setTaskCount(count)
  }
  const allTasks = subtasks.map((task) => {
    getTaskCount(task.length)
    return (
      <Tasks
        id={task.id}
        name={task.name}
        status={task.status}
        category={task.name}
        color={task.color}

      />
    );
  });

  return (
    <div className="main">
      <div className="MainHeader">
        <h1>{task.name}</h1>
        <h1></h1>
      </div>
      <div className="SecondMainHeader">
        <button>
          Add Task <FontAwesomeIcon icon={faCirclePlus} />
        </button>
        <select>
          <option>All</option>
          <option>Pending</option>
          <option>Done</option>
        </select>
      </div>

      <div className="TaskContainer">{allTasks}</div>
    </div>
  );
};

export default Main;
