import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCirclePlus } from "@fortawesome/free-solid-svg-icons";

import Tasks from "./Tasks";
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import FilterStatus from "./FilterStatus";
import NoTasks from "./NoTasks";
const Main = ({ taskLists, taskCount, getTaskID, handleTaskStatus,deleteTask }) => {
  const { taskID } = useParams();
  const lists = taskLists.filter((list) => list.id === taskID);
  const listObj = Object.assign({}, ...lists);
  const tasks = listObj.tasks;
  const allTasks = tasks.map((task) => {
    return (
      <Tasks
        taskID={taskID}
        id={task.id}
        name={task.name}
        status={task.status}
        handleTaskStatus={handleTaskStatus}
        deleteTask={deleteTask}

      />
    );
  });

  const getListID = () => {
    getTaskID(taskID);
  };


  return (
    <div className="main">
      <div className="MainHeader">
        {listObj.color ==='red'? <div></div>:""}
      </div>
      <div className="SecondMainHeader">
        <Link to="/add-task" onClick={getListID}>
          Add Task <FontAwesomeIcon icon={faCirclePlus} />
        </Link>

        <FilterStatus taskID={taskID} />
      </div>

      {tasks.length>0?<div className="TaskContainer">{allTasks}</div>:<NoTasks/>}
    </div>
  );
};

export default Main;
