import React, { useState } from "react";
import ReactDOM from "react-dom";
import { BrowserRouter, Route, withRouter } from "react-router-dom";

const FilterStatus = ({ taskID }) => {
  const [filter, setFilter] = useState("all");
  const handleFilter = (e) => {
    setFilter(e.tartget.value);
    // get status from Link
    //pass the taskID and setState as a url
  };
  const link = `${taskID}/${filter}`;
  return (
    <select onChange={handleFilter}>
      <option selected="selected" value={link}>
        All
      </option>
      <option value={link}>Pending</option>
      <option value={link}>Done</option>
    </select>
  );
};

export default FilterStatus;
