import { useState, useEffect } from "react";
import "./App.css";
import Main from "./components/Main";
import AddTask from "./components/Modal";
import { v4 as uuidv4 } from "uuid";
import { Route, Routes } from "react-router";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCirclePlus } from "@fortawesome/free-solid-svg-icons";
import Side from "./components/Side";

function App() {
  const [taskLists, setTaskLists] = useState([
    {
      id: uuidv4(),
      name: "Important",
      color: "red",
      tasks: [
        {
          id: uuidv4(),
          name: "Pay Bills",
          status: "Pending",
        },
        {
          id: uuidv4(),
          name: "Pay Rent",
          status: "Pending",
        },
        {
          id: uuidv4(),
          name: "Visit Dentist",
          status: "Pending",
        },
      ],
    },
    {
      id: uuidv4(),
      name: "Work",
      color: "yellow",
      tasks: [
        {
          id: uuidv4(),
          name: "Add a feature",
          status: "Done",
        },
        {
          id: uuidv4(),
          name: "Debug Code",
          status: "Pending",
        },
      ],
    },
    {
      id: uuidv4(),
      name: "Travel",
      color: "green",
      tasks: [
        {
          id: uuidv4(),
          name: "Book a Ticket",
          status: "Pending",
        },
      ],
    },
  ]);

  const [taskProperty, setTaskProperty] = useState({});
  const [subTasks, setSubTasks] = useState([]);
  const [taskCount, setTaskCount] = useState(0);

  const getTaskList = (id) => {
    const listsCopy = [...taskLists];
    const list = listsCopy.filter((copy) => copy.id === id);
    const listObj = Object.assign({}, ...list);
    setSubTasks(listObj.tasks);
    setTaskProperty(listObj);
  };

  return (
    <div className="container">
      <div className="grid">
        <div className="sidebar">
          <h1>You have 20 tasks</h1>
          <h1>Let's do this!</h1>
          <hr></hr>
          <div className="TaskList">
            <Side taskLists={taskLists} taskCount={taskCount}/>
          </div>

          <button>
            Add List <FontAwesomeIcon icon={faCirclePlus} />
          </button>
        </div>
        <Routes>
          <Route path=":taskID" element={<Main taskLists={taskLists} taskCount={taskCount} />}></Route>
          <Route path="/add" element={<AddTask/>}/>
        </Routes>

        {/* <SideBar
          taskLists={taskLists}
          getTaskList={getTaskList}
          taskCount={taskCount}
        /> */}
        {/* <Main subtasks={subTasks} task={taskProperty} taskCount={taskCount} /> */}
      </div>
    </div>
  );
}

export default App;
