import { useState, useEffect } from "react";
import "./App.css";
import Main from "./components/Main";
import SideBar from "./components/SideBar";
import Modal from "./components/Modal";
import { v4 as uuidv4 } from "uuid";
import { Route, Routes } from "react-router";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCirclePlus, faBars } from "@fortawesome/free-solid-svg-icons";
import TaskLists from "./components/TaskLists";

function App() {
  const [taskLists, setTaskLists] = useState([
    {
      id: uuidv4(),
      name: "Important",
      color: "red",
      tasks: [
        {
          id: uuidv4(),
          name: "Pay Bills",
          status: "Pending",
        },
        {
          id: uuidv4(),
          name: "Pay Rent",
          status: "Pending",
        },
        {
          id: uuidv4(),
          name: "Visit Dentist",
          status: "Pending",
        },
      ],
    },
    {
      id: uuidv4(),
      name: "Work",
      color: "yellow",
      tasks: [
        {
          id: uuidv4(),
          name: "Add a feature",
          status: "Done",
        },
        {
          id: uuidv4(),
          name: "Debug Code",
          status: "Pending",
        },
      ],
    },
  ]);

  const [taskProperty, setTaskProperty] = useState({});
  const [subTasks, setSubTasks] = useState([]);
  const [taskCount, setTaskCount] = useState(0);

  const getTaskList = (id) => {
    const listsCopy = [...taskLists];
    const list = listsCopy.filter((copy) => copy.id === id);
    const listObj = Object.assign({}, ...list);
    setSubTasks(listObj.tasks);
    setTaskProperty(listObj);
  };

  return (
    <div className="container">
      <div className="grid">
        <div className="sidebar">
          <h1>You have 20 tasks</h1>
          <h1>Let's do this!</h1>
          <hr></hr>
          <div className="TaskList">
            <ul>
              {taskLists.map((list) => (
                <li>
                  <Link to={list.id}>
                    <a>
                      <span>
                        <span className="task-menu-icon">
                          <FontAwesomeIcon icon={faBars} />
                        </span>
                        {list.name}
                      </span>
                      <span>{taskCount}</span>
                    </a>
                  </Link>
                </li>
              ))}
            </ul>
          </div>
          <button>
            Add List <FontAwesomeIcon icon={faCirclePlus} />
          </button>
        </div>

        {/* <SideBar
          taskLists={taskLists}
          getTaskList={getTaskList}
          taskCount={taskCount}
        /> */}
        {/* <Main subtasks={subTasks} task={taskProperty} taskCount={taskCount} /> */}
      </div>
    </div>
  );
}

export default App;
