import React, {useState} from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faRectangleXmark } from "@fortawesome/free-solid-svg-icons";
const AddTaskList = () => {
  const [task,setTask] = useState({
    id:'',
    name:'', 
    color:'', 
    tasks:[{}]
  });
  const getInput = (e)=>{
    switch(e.target.name){
      case 'name':setTask({...task,name:e.target.name})
    }
  }
  return (
    <div className="overlay">
      <div className="modal">
        <div className="modal-title">
          <h1>NEW LIST</h1>
          <hr></hr>
        </div>
        <div className="modal-body">
          <div>
            <label>
              List Name:
              <input type="text" name="name" onChange={getInput}></input>
            </label>
          </div>
          <div>
            <label>
              Color:
              <input type="radio" name="color" id="red-radio" value="red" onChange={getInput}></input>
              <input type="radio" name="color" id="yellow-radio" value="yellow" onChange={getInput}></input>
              <input type="radio" name="color" id="blue-radio" value="blue" onChange={getInput}></input>
              <input type="radio" name="color" id="green-radio" value="green" onChange={getInput}></input>
            </label>
          </div>
          <div></div>
        </div>
        <div className="modal-footer">
          <button>Cancel</button>
          <button>OK</button>
        </div>
      </div>
    </div>
  );
};

export default AddTaskList;
