import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCirclePlus } from "@fortawesome/free-solid-svg-icons";
import Tasks from "./Tasks";
import { useParams } from "react-router";
import { Link } from "react-router-dom";
const Main = ({ taskLists, taskCount, getTaskList, handleTaskStatus }) => {
  const params = new URLSearchParams(window.location.search);
  const { taskID } = useParams();

  const status = "pending";
  const lists = taskLists.filter((list) => list.id === taskID);
  const listObj = Object.assign({}, ...lists);
  const tasks = listObj.tasks;
  const statusTask = tasks.flat().filter((task) => task.status === status);

  const allTasks = tasks
    .flat()
    .filter((task) => task.status === status)
    .map((task) => {
      return (
        <Tasks
          taskID={taskID}
          id={task.id}
          name={task.name}
          status={task.status}
          handleTaskStatus={handleTaskStatus}
        />
      );
    });


  const getList = () => {
    getTaskList(taskID);
  };

  // const allTasks = subtasks.map((task) => {
  //   return (
  //     <Tasks
  //       id={task.id}
  //       name={task.name}
  //       status={task.status}
  //       category={task.name}
  //       color={task.color}
  //     />
  //   );
  // });

  return (
    <div className="main">
      <div className="MainHeader">
        <h1>{listObj.name}</h1>
        <h1>{taskCount}</h1>
      </div>
      <div className="SecondMainHeader">
        <Link to="/add-task" onClick={getList}>
          Add Task <FontAwesomeIcon icon={faCirclePlus} />
        </Link>

        <select>
          <option>All</option>
          <option>Pending</option>
          <option>Done</option>
        </select>
      </div>

      <div className="TaskContainer">{allTasks}</div>
    </div>
  );
};

export default Main;
