import React, { useState } from "react";
import { Link } from "react-router-dom";
const AddTask = ({ id, tasks }) => {
  const [newTask, setNewTask] = useState(tasks);
  const getInput = (e) => {
   e.preventDefault();
    const nameInput = e.target.value;
    const newTaskObj = {
        id:id,
        name:nameInput,
        status:"pending"
    }
    setNewTask(newTaskObj);
  };

  const addNewTask = (e) => {
    const allTask ={...tasks,newTask}
    setNewTask(allTask)

  };
  return (
    <div className="overlay">
      <div className="modal">
        <div className="modal-title">
          <h1>NEW TASK</h1>
          <hr></hr>
        </div>
        <div className="modal-body">
          <div>
            <label>List Name :</label>
            <input type="text" name="name" onChange={getInput}></input>
          </div>

          <div></div>
        </div>
        <div className="modal-footer">
          {/* <Link to="/" className="modal-cancel">
            Cancel
          </Link>
          */}
          <Link to='/' className="modal-ok" onClick={addNewTask}>
            OK
          </Link>
        </div>
      </div>
    </div>
  );
};

export default AddTask;
