import React from "react";
import TaskLists from "./TaskLists";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
const SideBar = ({ taskLists }) => {
  const allTasks = taskLists.map((list) => {
    return <TaskLists id={list.id} name={list.name} />;
  });

  return (
    <div className="sidebar">
      <h1>You have 20 tasks</h1>
      <h1>Let's do this!</h1>
      <hr></hr>
      <div className="TaskList">
        <ul>{allTasks}</ul>
      </div>
      <button>Add List<i class="fa-light fa-circle-plus"></i></button>
    </div>
  );
};

export default SideBar;
