import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import { BrowserRouter, Route, useNavigate } from "react-router-dom";
import FilterOptions from "./FilterOptions";

const FilterStatus = ({ taskID, status }) => {
  console.log(status)
  const optionArr = ['all','pending','done'];
  let options = []
  optionArr.forEach(option=>{
    if(option===status){
      options.unshift(option)
    }
    else{
      options.push(option)
    }
  })
  console.log()
  const optionDOM = options.map(option=>{
    return(<FilterOptions value={option}/>)
  })
  console.log(optionDOM)
  const navigate = useNavigate(); 
  const [selected,setSelected] = useState(status);

  const handleFilter = (e) => {

    const filterValue = e.target.value;
    navigate(`/${filterValue}/${taskID}`);

  };

  // FIXME: selected value does not change when a new category is clicked
  return (
    <select onChange={handleFilter}>
     {optionDOM}
    </select>
  );
};

export default FilterStatus;
