import { useState, useEffect } from "react";
import "./App.css";
import Main from "./components/Main";
import SideBar from "./components/SideBar";
import Modal from "./components/Modal";
import { v4 as uuidv4 } from "uuid";
import { Route, Routes } from "react-router";

function App() {
  const [taskLists, setTaskLists] = useState([
    {
      id: uuidv4(),
      name: "Important",
      color: "red",
      tasks: [
        {
          id: uuidv4(),
          name: "Pay Bills",
          status: "Pending",
        },
        {
          id: uuidv4(),
          name: "Pay Rent",
          status: "Pending",
        },
        {
          id: uuidv4(),
          name: "Visit Dentist",
          status: "Pending",
        },
      ],
    },
    {
      id: uuidv4(),
      name: "Work",
      color: "yellow",
      tasks: [
        {
          id: uuidv4(),
          name: "Add a feature",
          status: "Done",
        },
        {
          id: uuidv4(),
          name: "Debug Code",
          status: "Pending",
        },
      ],
    },
  ]);

  const [taskProperty, setTaskProperty] = useState({});
  const [subTasks, setSubTasks] = useState([]);
  const [taskCount, setTaskCount] = useState(0);

  const getTaskList = (id) => {
    const listsCopy = [...taskLists];
    const list = listsCopy.filter((copy) => copy.id === id);
    const listObj = Object.assign({}, ...list);
    setSubTasks(listObj.tasks);
    setTaskProperty(listObj);
  };

  return (
    <div className="container">
      <div className="grid">
        <Routes>
          <Route
            path="/sidebar"
            element={
              <SideBar
                taskLists={taskLists}
                getTaskList={getTaskList}
                taskCount={taskCount}
              />
            }
          ></Route>
        </Routes>

        <Main subtasks={subTasks} task={taskProperty} taskCount={taskCount} />
      </div>
    </div>
  );
}

export default App;
