import React, { useState } from "react";
import { useParams } from "react-router";

const UpdateTask = ({ taskLists }) => {
  const taskCopy = [...taskLists]
  const {taskID,id} = useParams();
  const taskToEdit = taskCopy.filter(copy=>copy.id===taskID).map(task=>task.tasks).flat().filter(task=>task.id===id).flat()
  const taskName = Object.assign({},...taskToEdit)
  const [taskEdit,setTaskEdit]= useState(taskName.name)
  const [newTask,setNewTask]=useState({
    taskID:taskID,
    id:id,
    name:taskEdit,
    status:'pending'
  })
  const getInput = (e)=>{
    e.preventDefault()
    let input = e.target.value;
    setTaskEdit(input);
  }

  const saveEdit=()=>{
    const newTaskCopy = [...newTask,]
  }


  return (
    <div className="overlay">
      <div className="modal">
        <div className="modal-title ">
          <h1>UPDATE TASK:</h1>
          <hr></hr>
        </div>
        <div className="modal-body">
          <div>
            <form autocomplete="off">
              <label>New Task Name:</label>
              <input type="text" name="name" value={taskEdit} onChange={getInput} ></input>
            </form>
          </div>

          <div></div>
        </div>
        <div className="modal-footer">
          * <Link to={"/" + listID} className="modal-cancel">
            Cancel
          </Link>

          <button className="modal-ok" onClick={saveEdit}>
            Save
          </button> *
        </div>
      </div>
    </div>
  );
};

export default UpdateTask;
