        <select>
          <option>All</option>
          <option>Pending</option>
          <option>Done</option>
        </select>
      </div>

      <div className="TaskContainer"><ul>

    {taskLists.map((list) => (
      <li>
        <Link to={list.id}>
          <span>
            <span className="task-menu-icon">
              <FontAwesomeIcon icon={faBars} />
            </span>

          </span>
          <span></span>
        </Link>
      </li>
    ))}
  </ul></div>
    </div>
  );
};

export default Main;
