  const [newTask, setNewTask] = useState([]);
  const getInput = (e) => {
    e.preventDefault();
    const nameInput = e.target.value;
    const newTaskObj = {
      id: id,
      listID: listID,
      name: nameInput,
      status: "pending",
    };
    const allTasks = [newTaskObj];
    setNewTask(allTasks);
  };

  const taskAdd = (e) => {

    if (newTask[0].name !== "") {
      addNewTask(listID, newTask);
      navigate(`/${listID}`);
    } else {
      alert("hoy")

    }
  };

  return (
    {listID}
  );
};

