import React from "react";
import TaskLists from "./TaskLists";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCirclePlus } from "@fortawesome/free-solid-svg-icons";
const SideBar = ({ taskLists, getTaskList,taskCount }) => {
  const allTasks = taskLists.map((list) => {
    return (
      <TaskLists id={list.id} name={list.name} getTaskList={getTaskList} />
    );
  });

  return (
    <div className="sidebar">
      <h1>You have 20 tasks</h1>
      <h1>Let's do this!</h1>
      <hr></hr>
      <div className="TaskList">
        <ul>{allTasks}</ul>
      </div>
      <button>
        Add List <FontAwesomeIcon icon={faCirclePlus} />
      </button>
    </div>
  );
};

export default SideBar;
