import React from "react";
import { useParams } from "react-router";

const UpdateTask = ({ taskLists }) => {
  const params = useParams();
  const taskID = params.taskID;
  const id = params.id;
  const [taskToEdit, setTaskToEdit] = {};
  const editTask = () => {
    const taskCopy = [...taskLists];
    const updateTask = taskCopy
      .filter((copy) => copy.id === taskID)
      .map((task) => task.tasks);
    console.log(updateTask);
  };

  return (
    <div className="overlay">
      <div className="modal">
        <div className="modal-title ">
          <h1>UPDATE TASK:</h1>
          <hr></hr>
        </div>
        <div className="modal-body">
          <div>
            <form autocomplete="off">
              <label>New Task Name:()</label>
              <input type="text" name="name"></input>
            </form>
          </div>

          <div></div>
        </div>
        <div className="modal-footer">
          {/* <Link to={"/" + listID} className="modal-cancel">
            Cancel
          </Link>

          <button className="modal-ok" onClick={taskAdd}>
            OK
          </button> */}
        </div>
      </div>
    </div>
  );
};

export default UpdateTask;
